                       if(even_tr.size()>0 || odd_tr.size()>0 ){
       stars=stars+"\n"+ $(byXpath('//*[@id="fullcredits_content"]/table[3]/tbody/tr['+(j)+']/td[2]')).getText()+"\n";
                           }
                           else{
                           j++}
